mapper.select:
SELECT count(1) FROM t_construction_type WHERE name=#{name} and parent_id=#{parentId} and is_delete=0

    and id!=#{id}

limit 1

SELECT count(1) FROM t_weak_plan WHERE name=#{name} and is_delete=0

    and id!=#{id}

limit 1

select c.id, c.plan_id, c.type_id, c.date, c.sort, c.no, c.org_id, c.start_time, c.end_time, c.is_day, c.name, c.region, c.electric_arrange, c.protect_measures, c.coordination_requirement, c.user_id, c.phone, c.remark, c.status, ct.name as type_name, wp.name as plan_name, d.org_name as org_name, ue.real_name as user_name from t_construction as c left join t_construction_type as ct on c.type_id=ct.id and ct.is_delete=0 left join t_weak_plan as wp on c.plan_id=wp.id and wp.is_delete=0 left join sys_dept as d on c.org_id=d.id and d.is_delete=0 left join sys_user_ext as ue on c.user_id=ue.user_id and ue.is_delete=0 where c.is_delete=0
and c.plan_id=#{planId}
order by c.date asc, c.sort asc, c.create_date desc

mapper.insert:
delete from t_construction where plan_id=#{planId} and is_delete=0; insert into t_construction(id, plan_id, type_id, date, sort, no, org_id, start_time, end_time, is_day, name, region, electric_arrange, protect_measures, coordination_requirement, user_id, phone, remark, create_by) values
(#{c.id}, #{planId}, (select id from t_construction_type where name=#{c.typeName} and is_delete=0), #{c.date}, #{c.sort}, #{c.no}, (select id from sys_dept where org_name=#{c.orgName} and is_delete=0), #{c.startTime}, #{c.endTime}, #{c.isDay}, #{c.name}, #{c.region}, #{c.electricArrange}, #{c.protectMeasures}, #{c.coordinationRequirement}, (select user_id from sys_user_ext where real_name=#{c.userName} and is_delete=0), #{c.phone}, #{c.remark}, #{c.createBy})
;
