mapper.select:
select p.id, p.no, p.hospital_no, p.type, p.start_time, p.end_time, p.remark, p.create_date, p.create_by, f1.file_url as file_word, f2.file_url as file_pdf, p.estimate_num, p.actual_num from t_physical as p left join sys_file as f1 on f1.id=p.file_word and f1.is_delete=0 left join sys_file as f2 on f2.id=p.file_pdf and f2.is_delete=0 where p.is_delete=0
and p.type=#{type}
and p.start_time>=#{sStartTime}
and #{sEndTime}>=p.start_time
and p.end_time>=#{eStartTime}
and #{eEndTime}>=p.end_time
and p.file_word is not null and p.estimate_num is not null and p.actual_num is not null
order by p.create_date desc

select p.id, p.no, p.hospital_no, p.type, p.start_time, p.end_time, p.remark, p.create_date, p.create_by, f1.file_url as file_word, f2.file_url as file_pdf, p.estimate_num, p.actual_num from t_physical as p left join sys_file as f1 on f1.id=p.file_word and f1.is_delete=0 left join sys_file as f2 on f2.id=p.file_pdf and f2.is_delete=0 where p.is_delete=0
and p.type=#{type}
and p.start_time>=#{sStartTime}
and #{sEndTime}>=p.start_time
and p.end_time>=#{eStartTime}
and #{eEndTime}>=p.end_time
and p.file_word is not null and p.estimate_num is not null and p.actual_num is not null
order by p.create_date desc

mapper.update:
update t_physical_result set
is_confirm=1, update_by=#{userId}
where physical_user_id=#{id} and is_delete=0

update t_physical_user set is_delete=1, update_by=#{createBy}
where physical_id=#{id} and physical_no=#{no} and is_delete=0;


        insert into t_physical_user(id, physical_id, physical_no,

            user_id,


            new_user_id,

        user_name, mobile, create_by)
        value (replace(uuid(),"-",""), #{id}, #{no},

            #{user.id},


            #{user.id},

        #{user.name}, #{user.mobile}, #{createBy});

            insert into t_physical_warning(id, type, physical_id, physical_user_id, warning_time, info)
            select replace(uuid(),"-",""), 4, #{id},
            (select id from t_physical_user where user_name=#{user.name}

                and mobile=#{user.mobile}

            and physical_id=#{id} and is_delete=0 and result is null and 3>status), now(), "离岗体检预警"
            from dual
            WHERE exists (select id from t_physical_user where user_name=#{user.name}

                and mobile=#{user.mobile}

            and physical_id=#{id} and is_delete=0 and result is null and 3>status);

update t_physical set
is_delete=1, update_by=#{createBy}
where id=#{id} and no=#{no} and is_delete=0;
update t_physical_user set is_delete=1, update_by=#{createBy}
where physical_id=#{id} and physical_no=#{no} and is_delete=0;

mapper.select:
select pu.id, pu.physical_id, pu.physical_no, pu.user_id, pu.new_user_id, pu.user_name, pu.is_review, u.main_dept as dept_id, pu.last_physical_user_id, pu.status, pu.result, pu.mobile, ue.user_no, p.type, d.org_name as dept_name, pu.physical_time, pu.create_date, f1.file_url as file_word, f2.file_url as file_pdf from t_physical_user as pu left join sys_user as u on u.id=pu.user_id and u.is_delete=0 left join sys_dept as d on d.id=u.main_dept and d.is_delete=0 left join sys_user_ext as ue on ue.user_id=pu.user_id and ue.is_delete=0 and ue.status=0 left join sys_file as f1 on f1.id=pu.file_word and f1.is_delete=0 left join sys_file as f2 on f2.id=pu.file_pdf and f2.is_delete=0 left join t_physical as p on p.id=pu.physical_id and p.is_delete=0 where pu.physical_id=#{id} and pu.is_delete=0
and pu.status=#{status}
and pu.result=#{result}
and u.main_dept=#{deptId}
order by pu.create_date desc, d.sort asc, pu.id asc

mapper.insert:
insert into t_physical_user(id, physical_id, physical_no, user_name, mobile, user_id, new_user_id,
is_review, last_physical_user_id, create_by)
select replace(uuid(),"-",""), #{physicalId}, #{physicalNo}, #{userName}, #{mobile}, #{userId}, #{newUserId}, 1, #{id}, #{createBy}
FROM DUAL
WHERE not exists (SELECT id FROM t_physical_user WHERE last_physical_user_id=#{id} and is_review=1 and is_delete=0);
update t_physical_user set
is_review=1, update_by=#{createBy}, status=5
where id=#{id} and is_delete=0;

mapper.select:
select user_id from t_physical_user
where user_id is not null and is_delete=0 and status=3

    and id in (

        #{id}

    )

select id from t_physical_user where user_name in (

    #{user.name}

)
and physical_id=#{id} and is_delete=0 and result is not null and 3=status

insert.foreach:

    insert into t_physical_warning(id, type, physical_id, physical_user_id, warning_time, info)
    select replace(uuid(),"-",""), 2, #{id},
    (select id from t_physical_user where user_name=#{user.name}

        and mobile=#{user.phone}

    and physical_id=#{id} and is_delete=0 and result is null and 3>status), now(), "职业禁忌症预警"
    from dual
    WHERE exists (select id from t_physical_user where user_name=#{user.name}

        and mobile=#{user.phone}

    and physical_id=#{id} and is_delete=0 and result is null and 3>status);


    insert into t_physical_warning(id, type, physical_id, physical_user_id, warning_time, info)
    select replace(uuid(),"-",""), 3, #{id},
    (select id from t_physical_user where user_name=#{user.name}

        and mobile=#{user.phone}

    and physical_id=#{id} and is_delete=0 and result is null and 3>status), now(), "体检复查预警"
    from dual
    WHERE exists (select id from t_physical_user where user_name=#{user.name}

        and mobile=#{user.phone}

    and physical_id=#{id} and is_delete=0 and result is null and 3>status);

insert into t_physical_result(id, physical_user_id, name, age, sex,

    phone,

work_year, work_type,
hazard_factor_name, warning_index, conclusion, handling_opinions, medical_advice, create_by)
select #{user.id}, (select id from t_physical_user where user_name=#{user.name}

    and mobile=#{user.phone}

and physical_id=#{id} and is_delete=0 and result is null and 3>status),
#{user.name}, #{user.age}, #{user.sex},

    #{user.phone},

#{user.workYear}, #{user.workType}, #{user.hazardFactorName},
#{user.warningIndex}, #{user.conclusion}, #{user.handlingOpinions}, #{user.medicalAdvice}, #{userId}
from dual
WHERE not exists (SELECT id FROM t_physical_result WHERE physical_user_id=(select id from t_physical_user
where user_name=#{user.name}

    and mobile=#{user.phone}

and physical_id=#{id} and is_delete=0 and result is null and 3>status) and is_delete=0);
update t_physical_user set
status=3, result=#{user.result}, update_by=#{userId}, physical_time=#{physicalTime}
where user_name=#{user.name}

    and mobile=#{user.phone}

and physical_id=#{id} and is_delete=0 and result is null and 3>status;

mapper.insert:
insert into t_physical_feedback(id, physical_id, physical_user_id, feedback_info, create_by)
value (#{id}, #{physicalId}, #{physicalUserId}, #{feedbackInfo}, #{createBy});
update t_physical_user set status=1 where id=#{physicalUserId};

update.foreach:
update t_physical_feedback set

    apply_opinions=#{feedback.applyOpinions},


    apply_time=now(), status=#{feedback.status}, audit_id=#{updateBy},

update_by=#{updateBy}
where physical_id=#{feedback.physicalId} and physical_user_id=#{feedback.physicalUserId} and is_delete=0;
update t_physical_user set status=2 where id=#{feedback.physicalUserId}

mapper.select:
SELECT distinct pw.id, pw.type, pw.info, pw.physical_id, pw.physical_user_id, pw.warning_time, pw.status, pu.user_name, ifnull(pu.user_id, pu.new_user_id) as user_id, p.type as physical_type, ue.user_no, sp.post_name, TIMESTAMPDIFF(DAY, p.end_time, now()) as day FROM t_physical_warning as pw left join t_physical as p on p.id=pw.physical_id and p.is_delete=0 left join t_physical_user as pu on pu.id=pw.physical_user_id and pu.is_delete=0 left join sys_user as u on u.id=pu.user_id and u.is_delete=0 left join sys_user_ext as ue on ue.user_id=pu.user_id and ue.is_delete=0 and ue.status=0 left join sys_dept as d on d.id=u.main_dept and d.is_delete=0 left join sys_user_post as up on up.user_id=pu.user_id left join sys_post as sp on sp.id=up.post_id and sp.is_delete=0 and sp.status=0 where pw.is_delete=0
and pw.type=#{type}
and d.id=#{deptId}
group by pw.id order by pw.create_date desc

