mapper.select:
select id, type, modules, risk_points, main_risk_points, describe_text, describe_result, describe_person_factor, describe_object_factor, describe_environment_factor, describe_manage_factor, describe_may_occur, accident_type, describe_occupational_hazards, risk_matrix_l, risk_matrix_s, risk_matrix_r, risk_evaluation_l, risk_evaluation_c, risk_evaluation_d, level, technical_measures, manage_measures, education_measures, individual_protection, emergency_measure, health_protect_measures, basis, responsibility_dept, responsibility_center, responsibility_post, responsibility_user, create_date, create_by, remark, status from t_risk_info where is_delete=0 and status=0
and type=#{type}
order by create_date desc

select count(1) from t_risk_info where is_delete=0 and type=#{type} and risk_points=#{riskPoints}
and modules=#{modules} and main_risk_points=#{mainRiskPoints}

    and describe_result=#{describeResult}


    and accident_type=#{accidentType}


    and describe_person_factor=#{describePersonFactor}


    and describe_object_factor=#{describeObjectFactor}


    and responsibility_dept=#{responsibilityDept}


    and responsibility_center=#{responsibilityCenter}


    and id!=#{id}

limit 1

update.if:
update t_risk_info set
is_delete=1, update_by=#{userId}, update_date=now()
where is_delete=0 and id in (

    #{id}

)

insert.foreach:
insert into t_risk_info(id, type, modules, risk_points, main_risk_points, describe_text, describe_result, accident_type,
describe_person_factor, describe_object_factor, describe_environment_factor, describe_manage_factor,
describe_may_occur, describe_occupational_hazards, risk_matrix_l, risk_matrix_s, risk_matrix_r,
risk_evaluation_l, risk_evaluation_c, risk_evaluation_d, level, technical_measures, manage_measures,
education_measures, individual_protection, emergency_measure, health_protect_measures, basis, responsibility_dept,
responsibility_center, responsibility_post, responsibility_user, remark, create_by)
select #{risk.id}, #{risk.type}, #{risk.modules}, #{risk.riskPoints}, #{risk.mainRiskPoints}, #{risk.describeText},
#{risk.describeResult}, #{risk.accidentType}, #{risk.describePersonFactor}, #{risk.describeObjectFactor}, #{risk.describeEnvironmentFactor},
#{risk.describeManageFactor}, #{risk.describeMayOccur}, #{risk.describeOccupationalHazards}, #{risk.riskMatrixL},
#{risk.riskMatrixS}, #{risk.riskMatrixR}, #{risk.riskEvaluationL}, #{risk.riskEvaluationC}, #{risk.riskEvaluationD},
#{risk.level}, #{risk.technicalMeasures}, #{risk.manageMeasures}, #{risk.educationMeasures},
#{risk.individualProtection}, #{risk.emergencyMeasure}, #{risk.healthProtectMeasures}, #{risk.basis},
#{risk.responsibilityDept}, #{risk.responsibilityCenter}, #{risk.responsibilityPost}, #{risk.responsibilityUser}, #{risk.remark}, #{risk.createBy}
from dual
WHERE not exists (SELECT id FROM t_risk_info WHERE type=#{risk.type} and modules=#{risk.modules} and
risk_points=#{risk.riskPoints} and main_risk_points=#{risk.mainRiskPoints} and is_delete=0

    and describe_result=#{risk.describeResult}


    and accident_type=#{risk.accidentType}


    and describe_person_factor=#{risk.describePersonFactor}


    and describe_object_factor=#{risk.describeObjectFactor}


    and responsibility_dept=#{risk.responsibilityDept}


    and responsibility_center=#{risk.responsibilityCenter}

)
